import { StyleSheet } from "react-native";

export default StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#facc15", // yellow background
    padding: 20,
  },

  wrapper: {
    maxWidth: 450,
    marginHorizontal: "auto",
  },

  // Header
  headerTitle: {
    fontSize: 24,
    fontWeight: "bold",
    color: "#111827", // dark text
    marginBottom: 4,
  },
  headerSub: {
    fontSize: 12,
    color: "#2563eb", // blueish sub text
    marginBottom: 2,
  },

  // Toggle
  toggleRow: {
    flexDirection: "row",
    justifyContent: "center",
    backgroundColor: "#fff",
    borderRadius: 20,
    padding: 4,
    marginVertical: 20,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 3,
  },
  toggle: {
    flex: 1,
    alignItems: "center",
    paddingVertical: 8,
    borderRadius: 16,
  },
  toggleActive: {
    backgroundColor: "#facc15",
    shadowColor: "#000",
    shadowOpacity: 0.15,
    shadowRadius: 3,
    elevation: 2,
  },
  toggleText: {
    fontSize: 14,
    fontWeight: "600",
    color: "#111827",
  },
  toggleTextActive: {
    fontSize: 14,
    fontWeight: "600",
    color: "#111827",
  },

  // Cards
  card: {
    backgroundColor: "#fff",
    borderRadius: 16,
    padding: 16,
    marginBottom: 20,
    shadowColor: "#000",
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 4,
  },
  cardTitle: {
    fontSize: 18,
    fontWeight: "700",
    color: "#111827",
    marginBottom: 12,
  },

  // Line chart legend
  legend: {
    fontSize: 14,
    color: "#111827",
    marginTop: 10,
  },

  // Insights
  insightsRow: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  pill: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#f9fafb",
    paddingVertical: 8,
    paddingHorizontal: 12,
    borderRadius: 20,
    shadowColor: "#000",
    shadowOpacity: 0.05,
    shadowRadius: 3,
    elevation: 2,
  },
  pillDot: {
    fontSize: 14,
    color: "#facc15",
    marginRight: 6,
  },
  pillText: {
    fontSize: 14,
    fontWeight: "600",
    color: "#111827",
  },

  // Footer
  footer: {
    textAlign: "center",
    fontSize: 12,
    color: "#374151",
    marginTop: 20,
    marginBottom: 40,
  },
});
