import { StyleSheet } from "react-native";

export default StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#facc15", // bright yellow background
    alignItems: "center",
    paddingVertical: 40,
  },
  wrapper: {
    width: "90%",
    maxWidth: 1200,
  },

  // Header
  headerTitle: {
    fontSize: 22,
    fontWeight: "700",
    marginBottom: 4,
    color: "#111827",
  },
  headerSub: {
    fontSize: 14,
    color: "#374151",
    marginBottom: 2,
  },

  // Toggle row
  toggleRow: {
    flexDirection: "row",
    backgroundColor: "#fff",
    borderRadius: 12,
    marginVertical: 20,
    padding: 4,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.15,
    shadowRadius: 4,
  },
  toggle: {
    flex: 1,
    paddingVertical: 12,
    borderRadius: 8,
    alignItems: "center",
  },
  toggleActive: {
    backgroundColor: "#f59e0b",
  },
  toggleText: {
    fontSize: 14,
    fontWeight: "600",
    color: "#111827",
  },
  toggleTextActive: {
    fontSize: 14,
    fontWeight: "600",
    color: "#fff",
  },

  // Row layout for cards
  row: {
    flexDirection: "row",
    justifyContent: "space-between",
    marginBottom: 30,
    gap: 20,
  },
  flex1: {
    flex: 1,
  },

  // Card
card: {
  backgroundColor: "#fff",
  borderRadius: 16,
  padding: 16,
  shadowColor: "#000",
  shadowOffset: { width: 0, height: 2 },
  shadowOpacity: 0.1,
  shadowRadius: 4,
  margin: 8,
  flex: 1,
},
  cardTitle: {
    fontSize: 18,
    fontWeight: "700",
    marginBottom: 12,
    color: "#111827",
  },

  // Legend
  legend: {
    fontSize: 14,
    marginTop: 8,
    color: "#111827",
  },

  // Insights pills
  insightsCard: {
  backgroundColor: "#fff",
  borderRadius: 16,
  padding: 16,
  shadowColor: "#000",
  shadowOffset: { width: 0, height: 2 },
  shadowOpacity: 0.1,
  shadowRadius: 4,
  flex: 1,
  maxHeight: 150,   // ✅ limit height
  justifyContent: "flex-start", // don’t stretch
},

  pill: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#f9fafb",
    borderRadius: 20,
    paddingVertical: 8,
    paddingHorizontal: 14,
    shadowColor: "#000",
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.05,
    shadowRadius: 3,
  },
  pillDot: {
    color: "#f59e0b",
    marginRight: 6,
  },
  pillText: {
    fontSize: 14,
    fontWeight: "600",
    color: "#111827",
  },

  // Footer
  footer: {
    fontSize: 14,
    color: "#374151",
    marginTop: 20,
    textAlign: "center",
  },
});
